import React from 'react';
import NewGameButton from './NewGameButton'
import Game from './Game'
import Scoreboard from './Scoreboard'
import data from './data'

class App extends React.Component {
  state={
    questionList: data.questions.slice(0, 3),
    scores: []
  }

  endGame = (data) => {
    let newQ = [data.questions[Math.floor(Math.random() * 10)], data.questions[Math.floor(Math.random() * 10)], data.questions[Math.floor(Math.random() * 10)]]
    console.log(newQ)
    this.setState(prevState => {return {scores: prevState.scores.concat(data)}})
    this.setState({questionList: newQ})
  }

  render(){
    return (
      <div>
        <h1>Lutrivia</h1>
        <NewGameButton />
        <Game questions={this.state.questionList} endGame={this.endGame}/>
        <Scoreboard scores={this.state.scores}/>
      </div>
    );
  }
}

export default App;
